/* Basic layout styles to match simple Figma layout: header, container, sidebar, main */
*{box-sizing:border-box}
body{font-family:Arial,Helvetica,sans-serif;margin:0;color:#222;background:#fff}
.container{max-width:1000px;margin:1rem auto;padding:0 1rem}
header{background:#f8f9fa;border-bottom:1px solid #e9ecef;padding:1rem}
header h1{font-size:1.1rem;margin:0}
nav a{color:#007bff;text-decoration:none;margin-right:0.5rem}
main.container{padding:1rem 1rem}
.sidebar{background:#f1f3f5;padding:1rem;border-right:1px solid #e9ecef}
.layout{display:flex;min-height:80vh}
.layout .layout-sidebar{width:220px}
.layout .layout-content{flex:1;padding:1rem}
@media(max-width:768px){
  .layout{flex-direction:column}
  .layout .layout-sidebar{width:100%}
}
/* simple button */
button{background:#007bff;color:#fff;border:none;padding:0.4rem 0.7rem;border-radius:4px;cursor:pointer}
button:disabled{opacity:.6}
