:root{
  --primary:#663399;
  --accent:#FFCC00;
  --danger:#CC0000;
}
*{box-sizing:border-box}
body{font-family:Inter,system-ui,Arial,Helvetica,sans-serif;margin:0;background:#f7f7fb;color:#111}

/* Header */
header.fixed{height:64px}
header{background:var(--primary);height:64px;color:#fff;position:fixed;top:0;left:0;right:0;z-index:60;box-shadow:0 2px 8px rgba(0,0,0,0.12)}
header .max-w-full{display:flex;align-items:center;justify-content:space-between;height:100%}
header button{background:transparent;border:0;color:#fff;cursor:pointer}
header .login-brand{font-weight:700}

.content{padding-top:64px}

/* Burger menu */
.pengurus-overlay{position:fixed;inset:0;background:rgba(0,0,0,0.45);z-index:55}
.pengurus-overlay.hidden{display:none}
.pengurus-burger{position:fixed;left:0;top:0;bottom:0;width:300px;background:#FFCC00;color:#111;transform:translateX(-110%);transition:transform .28s ease;z-index:56;padding:16px}
.pengurus-burger[aria-hidden="false"]{transform:translateX(0)}
.burger-header{display:flex;justify-content:space-between;align-items:center;margin-bottom:12px}
.burger-title{font-size:18px;font-weight:700}
.burger-nav ul{list-style:none;padding:0;margin:0}
.burger-nav li{margin:10px 0}
.burger-nav a{color:inherit;text-decoration:none;font-weight:600}

/* Layout cards and forms */
.pengurus-login-wrap{min-height:calc(100vh - 64px);display:flex;align-items:center;justify-content:center;padding:32px}
.pengurus-login-card{width:380px;background:#fff;padding:28px;border-radius:14px;box-shadow:0 8px 24px rgba(0,0,0,0.08);text-align:center}
.login-title{font-size:20px;margin:8px 0 18px}
.form-label{display:block;text-align:left;margin:8px 0 6px;font-weight:600}
.form-input{width:100%;padding:10px;border:1px solid #e6e6f0;border-radius:8px}
.btn-primary{background:var(--primary);color:#fff;border:0;padding:10px 14px;border-radius:10px;cursor:pointer;margin-top:12px}
.btn-sm{padding:6px 10px;border-radius:8px;background:#eee;border:0;cursor:pointer}
.btn-secondary{background:#f3f3f6}
.btn-danger{background:var(--danger);color:#fff}

.page-title{font-size:28px;margin:0 0 18px}
.stats-grid{display:flex;gap:16px}
.stat-card{background:#fff;padding:18px;border-radius:12px;flex:1;box-shadow:0 6px 18px rgba(0,0,0,0.06)}
.stat-value{font-size:22px;font-weight:700}
.stat-label{color:#666}

.card{background:#fff;border-radius:12px;padding:16px;box-shadow:0 6px 18px rgba(0,0,0,0.06)}
.card-title{font-size:18px;margin:0 0 8px}

.profil-grid{display:grid;grid-template-columns:1fr 380px;gap:20px}
.logo-placeholder{width:84px;height:84px;background:#f3f3f6;border-radius:8px;display:flex;align-items:center;justify-content:center}
.banner-placeholder{height:84px;background:#eae6ff;border-radius:8px;flex:1;margin-left:12px;display:flex;align-items:center;justify-content:center}
.card-row{display:flex;align-items:center;gap:12px}
.form-row{margin-top:12px}
.stat-large{font-size:20px;font-weight:700}

.editable-block .edit-mode.hidden,.prog-detail.hidden{display:none}

.program-list{list-style:none;padding:0;margin:0}
.program-list li{border-bottom:1px solid #f0f0f6;padding:10px 0}
.prog-row{display:flex;justify-content:space-between;align-items:center}

.social-list{list-style:none;padding:0;margin:0}

/* small responsive */
@media(max-width:900px){.profil-grid{grid-template-columns:1fr}}
